import React, { useState } from 'react';
import NavBar from './NavBar';
import Buttons from './Buttons';
import styles from './styles.module.css';
import errorIcon from './error.svg';
import correctIcon from './correct.svg';


const SignIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const validateEmail = (value) => {
    const regex = /\S+@\S+\.\S+/;
    return regex.test(value);
  };

  const handleEmailChange = (e) => {
    const value = e.target.value;
    setEmail(value);
    if (!validateEmail(value)) {
      setEmailError('Please enter a valid email address.');
    } else {
      setEmailError('');
    }
  };


  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);
    if (value.length < 8) {
      setPasswordError('Password must be at least 8 characters long.');
    } else {
      setPasswordError('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="sign-in-container">
      <NavBar title="Sign In" />
      <form className="sign-in-form" onSubmit={handleSubmit}>
        <label htmlFor="email">EMAIL OR NUMBER:</label>
        <input type="text" id="email" placeholder="Enter your email address" value={email} onChange={handleEmailChange} />
        {emailError && <div className="error-message">{emailError}</div>}

        <label htmlFor="password">PASSWORD:</label>
        <input type="password" id="password" placeholder="Enter your 8-digit password" value={password} onChange={handlePasswordChange} />
        {passwordError && <div className="error-message">{passwordError}</div>}

        <a href="/forgot-password" className="forgot-password-link">Forgot your password?</a>
        
        <Buttons text="Log In" onClick={() => {/* Handle login */}} />
        <div className="secondary-action">
          Need an account? <a href="/sign-up" className="sign-up-link">Sign up</a>
        </div>
      </form>
    </div>
  );
};

export default SignIn;
