import React from 'react';
import styles from './styles.module.css';
import Button from '../../Components/Button';

const SignIn = () => {
    
  return (
    <div className={styles.container}>

    <div className={styles.leftContainer}>
    <img src={process.env.PUBLIC_URL + '/images/background.png'} alt="Cover Image" className={styles.coverImage} />
    </div>    

    <div className={styles.rightContainer}>
    <div className="sign-in-container">
      <NavBar title="Sign In" />
      <form className="sign-in-form" onSubmit={handleSubmit}>
        <label htmlFor="email">EMAIL OR NUMBER:</label>
        <input type="text" id="email" placeholder="Enter your email address" value={email} onChange={handleEmailChange} />
        {emailError && <div className="error-message">{emailError}</div>}

        <label htmlFor="password">PASSWORD:</label>
        <input type="password" id="password" placeholder="Enter your 8-digit password" value={password} onChange={handlePasswordChange} />
        {passwordError && <div className="error-message">{passwordError}</div>}

        <a href="/forgot-password" className="forgot-password-link">Forgot your password?</a>
        
        <Buttons text="Log In" onClick={() => {/* Handle login */}} />
        <div className="secondary-action">
          Need an account? <a href="/sign-up" className="sign-up-link">Sign up</a>
        </div>
      </form>
    </div>
    </div>

    </div>

);
};

export default SignIn;
